import Vue from 'vue'
import Router from 'vue-router'
import HelloContainer from "./components/HelloWorld.vue"
//src/router.js 配置组件访问路由
//1:引入自定义组件
import HomeContainer from "./components/tabbar/HomeContainer";
import NewsContainer from "./components/news/NewsContainer";
import MemberContainer from "./components/tabbar/MemberContainer";
import ShopcarContainer from "./components/tabbar/ShopcarContainer";
import SearchContainer from "./components/tabbar/SearchContainer";
import NewsDetailsContainer from "./components/news/NewsDetailsContainer";
import Goodlist from "./components/goods/Goodlist";
import Goodsinfo from "./components/goods/Goodsinfo"
Vue.use(Router)
//2:指定该组件访问路径
export default new Router({
  routes: [
    {path:'/',redirect:"/home"},
    {path:'/home',component:HomeContainer},
    {path:'/home/newslist',component:NewsContainer},
    {path:'/home/goodlist/:id',component:Goodlist},
    {path:'/member',component:MemberContainer},
    {path:'/shopcar',component:ShopcarContainer},
    {path:'/search',component:SearchContainer},
    {path:'/newsdetail/:id',component:NewsDetailsContainer},
    {path:'/home/goodsinfo',component:Goodsinfo,name:"goodsinfo"}
  ],linkActiveClass:"mui-active"
})
